mapper.select:
SELECT 

FROM zlmt_worksheet_note a

WHERE a.id = #{id}

SELECT 

FROM zlmt_worksheet_note a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zlmt_worksheet_note a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zlmt_worksheet_note SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
